      let hpos = Math.floor(Math.random() * this.sizeH);
      let lpos = Math.floor(Math.random() * this.sizeL);
      let land = this.territory[hpos][lpos];

      if (this.weather === "sun" || this.weather === "rain") {
        if (land && land.getType() == 1) {
          if (land.getConf() < 15) {
            land.increaseConf();
          } else {
            land.setType(0);
            land.resetConf();
          }
        }
      } else {
        if (land && land.getType() == 1) {
          if (land.getConf() > 0) {
            land.decreaseConf();
          }
        }
