  }

  movePenguins() {
    this.penguins.forEach(penguin => {
      if (penguin.isAlive()) {
        let lmoves = [1, -1, 0, 0, 1, 1, -1, -1];
        let hmoves = [0, 0, 1, -1, 1, -1, 1, -1];
