const penguinReq = require("./penguin.js");

const axios = require("axios");

let Penguin = penguinReq.Penguin;

class Land {
  constructor(l, h) {
    // console.log(x + " " + y);
    this.lpos = l;
    this.hpos = h;
    this.type = 0;
    this.conf = 0;
    this.hasCross = false;
    this.hasFish = false;
    this.penguin = null;
  }

  setLand(num) {
    this.type = num;
  }

  addPenguin(penguin) {
    this.penguin = penguin;
  }

  checkPenguin() {
    return this.penguin;
  }

  getType() {
    return this.type;
  }

  setType(newType) {
    this.type = newType;
  }

